## Script Folders - Create Store Proc ##
DELIMITER $$
DROP PROCEDURE IF EXISTS `tmpScriptFolderCheckProc`$$
CREATE PROCEDURE tmpScriptFolderCheckProc()
BEGIN
    DECLARE DeviceMonScrptFldrID INT(20);
    DECLARE DeviceMonFunScrptFldrID INT(20);
    SET DeviceMonScrptFldrID = (SELECT `FolderID` FROM `ScriptFolders` WHERE `Name` = 'Device Monitoring' ORDER BY `FolderID` DESC LIMIT 1);
        IF DeviceMonScrptFldrID IS NULL THEN 
        INSERT INTO `scriptfolders` (`FolderID`, `ParentID`, `Name`) VALUES(NULL,'0','Device Monitoring');
    END IF;
    SET DeviceMonScrptFldrID = (SELECT `FolderID` FROM `ScriptFolders` WHERE `Name` = 'Device Monitoring' ORDER BY `FolderID` DESC LIMIT 1);

    SET DeviceMonFunScrptFldrID = (SELECT `FolderID` FROM `ScriptFolders` WHERE `Name` = 'Functions' AND `ParentID` = DeviceMonScrptFldrID ORDER BY `FolderID` DESC LIMIT 1);
    IF DeviceMonFunScrptFldrID IS NULL THEN 
        INSERT INTO `scriptfolders` (`FolderID`, `ParentID`, `Name`) VALUES(NULL,DeviceMonScrptFldrID,'Functions');
    END IF;
END$$
DELIMITER ;

## Script Folders - Call then Drop Store Proc ##
CALL tmpScriptFolderCheckProc();
SET @DeviceMonScrptFldrID = (SELECT `FolderID` FROM `ScriptFolders` WHERE `Name` = 'Device Monitoring' ORDER BY `FolderID` DESC LIMIT 1);
SET @DeviceMonFunScrptFldrID = (SELECT `FolderID` FROM `ScriptFolders` WHERE `Name` = 'Functions' AND `ParentID` = @DeviceMonScrptFldrID ORDER BY `FolderID` DESC LIMIT 1);
DROP PROCEDURE IF EXISTS `tmpScriptFolderCheckProc`;

